<!DOCTYPE html><html><head>  <meta charset="utf-8">  <meta http-equiv="X-UA-Compatible" content="IE=edge">  <title>Torre Control | Login</title>  <!-- Tell the browser to be responsive to screen width -->  <meta name="viewport" content="width=device-width, initial-scale=1">    	<script src="/plugins/jquery/jquery.min.js"></script>	<script type="text/javascript" src="js/login_sha512.js"></script>	<script type="text/javascript" src="js/login_forms.js"></script>	  <!-- Font Awesome -->  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/font-awesome/4.4.0/css/font-awesome.min.css">  <!-- Ionicons -->  <link rel="stylesheet" href="https://code.ionicframework.com/ionicons/2.0.1/css/ionicons.min.css">  <!-- Theme style -->  <link rel="stylesheet" href="dist/css/adminlte.min.css">  <!-- Google Font: Source Sans Pro -->  <link href="https://fonts.googleapis.com/css?family=Source+Sans+Pro:300,400,400i,700" rel="stylesheet"></head><body class="hold-transition login-page">	<div class="login-box">	  <div class="login-logo">		<a href="#"><b>Torre</b>Login</a>	  </div>	  <!-- /.login-logo -->	  <div class="card">		<div class="card-body login-card-body">		  <p class="login-box-msg">Iniciar Sesion </p>		  <form action="php\default\login_process.php" method="post">			<div class="form-group has-feedback">			  <input name= "email" type="email" class="form-control" placeholder="Email" >			  <span class="fa fa-envelope form-control-feedback"></span>			</div>			<div class="form-group has-feedback">			  <input name = "password" type="password" class="form-control" placeholder="Password">			  <span class="fa fa-lock form-control-feedback"></span>			</div>			<div class="row">			  <div class="col-12">				<button type="submit" onclick="formhash(this.form, this.form.password);" class="btn btn-primary btn-block btn-flat">Ingresar</button>			  </div>			  <!-- /.col -->			</div>		  </form>		</div>		<!-- /.login-card-body -->	  </div>	</div>	<!-- /.login-box -->	<!-- jQuery -->	<script src="plugins/jquery/jquery.min.js"></script>	<!-- Bootstrap 4 -->	<script src="plugins/bootstrap/js/bootstrap.bundle.min.js"></script></body></html>